INSERT INTO esrb(
            short_desc, full_desc, image_url)
    VALUES ('Early Childhood', 'Content is intended for young children', 'http://www.esrb.org/images/ratingsymbol_ec.png'),
    ('Everyone', 'Content is generally suitable for all ages. May contain minimal cartoon, fantasy or mild violence and/or infrequent use of mild language.', 'http://www.esrb.org/images/ratingsymbol_e.png'),
    ('Everyone 10+', 'Content is generally suitable for ages 10 and up. May contain more cartoon, fantasy or mild violence, mild language and/or minimal suggestive themes.', 'http://www.esrb.org/images/ratingsymbol_e10.png'),
    ('Teen', 'Content is generally suitable for ages 13 and up. May contain violence, suggestive themes, crude humor, minimal blood, simulated gambling and/or infrequent use of strong language.', 'http://www.esrb.org/images/ratingsymbol_t.png'),
    ('Mature', 'Content is generally suitable for ages 17 and up. May contain intense violence, blood and gore, sexual content and/or strong language.', 'http://www.esrb.org/images/ratingsymbol_m.png'),
    ('Adults Only', 'Content suitable only for adults ages 18 and up. May include prolonged scenes of intense violence, graphic sexual content and/or gambling with real currency.', 'http://www.esrb.org/images/ratingsymbol_ao.png'),
    ('Rating Pending', 'Not yet assigned a final ESRB rating. Appears only in advertising, marketing and promotional materials related to a game that is expected to carry an ESRB rating, and should be replaced by a game''s rating once it has been assigned.', 'http://www.esrb.org/images/ratingsymbol_rp.png');
